package Programs.Chapter_1;

public class Ch1_04_Data_Types
{
    public static void main(String []args)
    {
        byte var1 = 127; // -128 to 127
        short var2 = 29999; // -30000 to 29999
        int var3 = 2099999999; // -1999999999 to 2099999999
        long var4 = -1999999999; // -1999999999 to 2099999999

        float var5 = -299999999999999999999999999999999999999.9f; // -299999999999999999999999999999999999999.9fto 299999999999999999999999999999999999999.9
        double var6 = .9; // -.9 to .9

        char var7 = 'a'; // 'A' to 'Z' , 'a' to 'z' , and other special symbols
        boolean var8 = true; // true or false

        System.out.println("The value of Byte var1 is : "+ var1);
        System.out.println("The value of Short var2 is : "+ var2);
        System.out.println("The value of Integer var3 is : "+ var3);
        System.out.println("The value of Long var4 is : "+ var4);
        System.out.println("The value of Float var5 is : "+ var5);
        System.out.println("The value of Double var6 is : "+ var6);
        System.out.println("The value of Character var7 is : "+ var7);
        System.out.println("The value of Boolean var8 is : "+ var8);
    }
}